mapper.select:
	SELECT count(*)
    from rental
    where user_id = #{user_id}
    	and tool_code = #{tool_code}
        and rental_state != '반납'

		select TO_DATE(sysdate, 'YYYY-MM-DD') - TO_DATE(return_date, 'YYYY-MM-DD') 
			from rental 
    		where rental_id = #{rental_id}

mapper.update:
update rental
	set rental_state = '반납'
	where rental_id = #{rental_id}
